import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // サーバーからマインドマップ取得
  const fetchMindmap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`);
      // もし取得データが空の場合はサンプルを設定（デバッグ用）
      if (!res.data) {
        throw new Error('データが存在しません');
      }
      setMindmap(res.data);
      setTitle(res.data.title);
      setError(null);
    } catch (err) {
      console.error(err);
      setError('マインドマップの取得に失敗しました');
      // デバッグ用：存在しない場合、以下のようなサンプルデータを設定して確認する
      // setMindmap({
      //   id: 'sample_mindmap',
      //   title: 'サンプルマインドマップ',
      //   nodes: [{
      //     nodeId: 'node_1',
      //     text: 'テーマ',
      //     x: 200,
      //     y: 100,
      //     width: 150,
      //     height: 80
      //   }],
      //   edges: [],
      //   createdAt: new Date().toISOString()
      // });
    }
  };

  useEffect(() => {
    if (mindmapId) {
      fetchMindmap();
    }
  }, [mindmapId]);

  // 保存処理：nodes と edges を含む
  const handleSave = async () => {
    if (!mindmap) return;
    try {
      await axios.put(`${SERVER_URL}/api/mindmaps/${mindmap.id}`, {
        title,
        nodes: mindmap.nodes,
        edges: mindmap.edges
      });
      alert('保存しました！');
    } catch (err) {
      console.error(err);
      alert('保存に失敗しました');
    }
  };

  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h1>エラー</h1>
        <p>{error}</p>
        <button onClick={fetchMindmap}>再取得</button>
      </div>
    );
  }

  if (!mindmap) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>マインドマップ編集</h1>
      <div style={{ marginBottom: 10 }}>
        <label>タイトル: </label>
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ width: 200 }}
        />
      </div>
      <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
      <div style={{ marginTop: 20 }}>
        <button onClick={handleSave}>保存</button>
        {/* Export Markdown ページへ遷移するボタン */}
        <button
          onClick={() => navigate(`/export/${mindmap.id}`)}
          style={{ marginLeft: 10 }}
        >
          Export Markdown
        </button>
      </div>
    </div>
  );
}

export default Editor;
