// client/src/pages/Editor.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const token = localStorage.getItem('token');
  const navigate = useNavigate();

  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const [error, setError] = useState(null);
  const [isEditingTitle, setIsEditingTitle] = useState(false);
  const titleInputRef = useRef(null);

  // ありがとうカウント
  const [thankCount, setThankCount] = useState(0);
  // ありがとう送信ボタンの連打防止
  const [thankDisabled, setThankDisabled] = useState(false);

  // ★ 変更：トークン取得
  const tokenRef = useRef(localStorage.getItem('token'));

  useEffect(() => {
    // ★ 変更：もしトークンが無ければログイン画面へ
    if (!tokenRef.current) {
      alert('ログインが必要です');
      navigate('/login');
      return;
    }
    if (mindmapId) {
      fetchMindmap();
      fetchThankCount();
    }
    // eslint-disable-next-line
  }, [mindmapId]);

  // Mindmap 取得
  const fetchMindmap = async () => {
    try {
      // ★ 変更：Authorizationヘッダー付きでGET
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`, {
        headers: { Authorization: `Bearer ${tokenRef.current}` }
      });
      setMindmap(res.data);
      setTitle(res.data.title);
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.response ? err.response.data.error : err.message);
      setMindmap(null);
    }
  };

  // ありがとう数取得 (こちらはトークン不要ならヘッダー無しでもOK)
  const fetchThankCount = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/thank/${mindmapId}`);
      setThankCount(res.data.count);
    } catch (err) {
      console.error(err);
    }
  };

  // 保存
  const handleSave = async () => {
    if (!mindmap) return;
    try {
      // ★ 変更：PUT時にもAuthorizationを付与
      await axios.put(`${SERVER_URL}/api/mindmaps/${mindmap.id}`, 
               { title, nodes: mindmap.nodes, edges: mindmap.edges },
              { headers: { Authorization: `Bearer ${token}` } }
              );
      alert('保存しました！');
    } catch (err) {
      console.error(err);
      alert('保存に失敗しました');
    }
  };

  // URL共有（クリップボードコピー）
  const handleShare = () => {
    const shareUrl = window.location.href;
    navigator.clipboard.writeText(shareUrl).then(() => {
      alert('リンクをコピーしました');
    });
  };

  // ホームへ戻る
  const handleGoHome = () => {
    navigate('/mypage');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // ありがとう送信（1分間無効化）
  const handleSendThank = async () => {
    if (thankDisabled) return;
    try {
      // ありがとう送信はトークン不要でOKならヘッダー無し
      // 必要なら付与
      await axios.post(`${SERVER_URL}/api/thank`, { mindmapId });
      await fetchThankCount();
      alert('ありがとうを送りました！');
      setThankDisabled(true);
      setTimeout(() => {
        setThankDisabled(false);
      }, 60000); // 1分後に再度有効化
    } catch (err) {
      console.error(err);
      alert('ありがとう送信に失敗しました');
    }
  };

  // タイトル編集
  const handleTitleDoubleClick = () => {
    setIsEditingTitle(true);
  };
  const handleTitleBlur = () => {
    setIsEditingTitle(false);
  };
  const handleTitleKeyDown = (e) => {
    if (e.key === 'Enter') {
      setIsEditingTitle(false);
    }
  };

  // エラー表示
  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h1>エラー</h1>
        <p>{error}</p>
        <button onClick={fetchMindmap}>再取得</button>
      </div>
    );
  }

  // ローディング表示
  if (!mindmap) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {/* ヘッダー */}
      <header
        style={{
          padding: '10px 20px',
          background: '#f5f5f5',
          borderBottom: '1px solid #ccc',
          display: 'flex',
          alignItems: 'center'
        }}
      >
        {/* ホームへ戻るボタン */}
        <button
          onClick={handleGoHome}
          style={{
            width: 40,
            height: 40,
            borderRadius: '50%',
            border: 'none',
            background: '#007BFF',
            color: '#fff',
            fontSize: '20px',
            marginRight: 10,
            cursor: 'pointer'
          }}
        >
          ←
        </button>

        {/* タイトル：ダブルクリックで編集モード */}
        {isEditingTitle ? (
          <input
            ref={titleInputRef}
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            onBlur={handleTitleBlur}
            onKeyDown={handleTitleKeyDown}
            style={{
              fontSize: '24px',
              flexGrow: 1,
              border: '1px solid #ccc',
              borderRadius: '4px',
              padding: '4px 8px'
            }}
          />
        ) : (
          <h2
            onDoubleClick={handleTitleDoubleClick}
            style={{ flexGrow: 1, margin: 0, fontSize: '24px', cursor: 'text' }}
            title="ダブルクリックでタイトルを編集できます"
          >
            {title}
          </h2>
        )}

        {/* SNS共有ボタン */}
        <button
          onClick={handleShare}
          style={{
            marginLeft: 10,
            border: 'none',
            background: '#28a745',
            color: '#fff',
            padding: '8px 12px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          リンクを共有
        </button>
      </header>

      {/* メインコンテンツ */}
      <main style={{ padding: '20px', flexGrow: 1 }}>
        {/* 操作用: ありがとう数、ありがとうボタン */}
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: 16 }}>
          {/* ありがとうカウント */}
          <div style={{ fontSize: '32px', fontWeight: 'bold', marginRight: 10 }}>
            {thankCount}
          </div>
          {/* ありがとう送信ボタン */}
          <button
            onClick={handleSendThank}
            disabled={thankDisabled}
            style={{
              border: '1px solid #ccc',
              background: thankDisabled ? '#e0e0e0' : '#f0f0f0',
              padding: '8px 12px',
              cursor: thankDisabled ? 'not-allowed' : 'pointer',
              borderRadius: '4px'
            }}
            title="ありがとうを送る（1分間に一度のみ送れます）"
          >
            ↑
          </button>
          {/* Spacer */}
          <div style={{ marginLeft: 50 }}>
            <small style={{ color: '#666' }}>
              ※タイトルをダブルクリックで編集できます
            </small>
          </div>
        </div>

        {/* キャンバス領域 */}
        <div
          style={{
            position: 'relative',
            border: '1px solid #ccc',
            width: '100%',
            maxWidth: '1000px', // 必要に応じて調整
            height: '545px',
            overflow: 'auto',
            marginBottom: 20
          }}
        >
          <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
        </div>

        {/* 操作用ボタン類 */}
        <div style={{ display: 'flex', gap: '10px' }}>
          <button
            onClick={handleSave}
            style={{
              background: '#007BFF',
              color: '#fff',
              border: 'none',
              padding: '10px 16px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            保存
          </button>
          <button
            onClick={() => navigate(`/export/${mindmap.id}`)}
            style={{
              background: '#6c757d',
              color: '#fff',
              border: 'none',
              padding: '10px 16px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Export Markdown
          </button>
        </div>
      </main>

      {/* フッター */}
      <footer
        style={{
          background: '#f5f5f5',
          borderTop: '1px solid #ccc',
          padding: '10px 20px',
          textAlign: 'center'
        }}
      >
        <small style={{ color: '#888' }}>
          &copy; 2025 MindMap App. All rights reserved.
        </small>
      </footer>
    </div>
  );
}

export default Editor;
