// client/src/components/ThankButton.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { getAnonymousId } from '../utils/anonymousId';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function ThankButton({ mindmapId }) {
  const [isDisabled, setIsDisabled] = useState(false);
  const [thankCount, setThankCount] = useState(0);

  useEffect(() => {
    fetchThankCount();
  }, []);

  const fetchThankCount = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/thank/${mindmapId}`);
      setThankCount(res.data.count);
    } catch (err) {
      console.error(err);
    }
  };

  const handleThank = async () => {
    if (isDisabled) return;
    setIsDisabled(true);
    // ここで必要なら匿名IDも利用可能（例: const anonymousId = getAnonymousId();）
    try {
      await axios.post(`${SERVER_URL}/api/thank`, { mindmapId });
      await fetchThankCount();
      alert('ありがとうを送りました！');
      setTimeout(() => {
        setIsDisabled(false);
      }, 60000); // 1分間無効化
    } catch (err) {
      console.error(err);
      setIsDisabled(false);
    }
  };

  return (
    <div>
      <button onClick={handleThank} disabled={isDisabled}>
        ありがとうを送る
      </button>
      <p style={{ fontSize: '24px', margin: '5px 0' }}>現在のありがとう数: {thankCount}</p>
      {isDisabled && <p style={{ color: 'red' }}>1分間は連続で押せません</p>}
    </div>
  );
}

export default ThankButton;
