// client/src/pages/Editor.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const navigate = useNavigate();

  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const [error, setError] = useState(null);
  const [isEditingTitle, setIsEditingTitle] = useState(false);
  const titleInputRef = useRef(null);

  const [thankCount, setThankCount] = useState(0);
  const [thankDisabled, setThankDisabled] = useState(false);

  // コピー/保存メッセージ
  const [copyMessage, setCopyMessage] = useState('');
  const [saveMessage, setSaveMessage] = useState('');

  // トークン
  const token = localStorage.getItem('token');

  // ライフサイクル
  useEffect(() => {
    if (!token) {
      alert('ログインが必要です');
      navigate('/login');
      return;
    }
    if (mindmapId) {
      fetchMindmap();
      fetchThankCount();
    }
    // eslint-disable-next-line
  }, [mindmapId]);

  // ---------------------------------------
  // API calls
  // ---------------------------------------
  const fetchMindmap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMindmap(res.data);
      setTitle(res.data.title);
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.response ? err.response.data.error : err.message);
      setMindmap(null);
    }
  };

  const fetchThankCount = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/thank/${mindmapId}`);
      setThankCount(res.data.count);
    } catch (err) {
      console.error(err);
    }
  };

  // 保存
  const handleSave = async () => {
    if (!mindmap) return;
    try {
      await axios.put(
        `${SERVER_URL}/api/mindmaps/${mindmap.id}`,
        { title, nodes: mindmap.nodes, edges: mindmap.edges },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      showSaveMessage('保存しました！');
    } catch (err) {
      console.error(err);
      if (err.response && err.response.status === 403) {
        showSaveMessage('他人のマップです。編集権限がありません', true);
      } else {
        showSaveMessage('保存に失敗しました', true);
      }
    }
  };

  // ありがとう送信
  const handleSendThank = async () => {
    if (thankDisabled) return;
    try {
      await axios.post(`${SERVER_URL}/api/thank`, { mindmapId });
      await fetchThankCount();
      showCopyMessage('ありがとうを送りました！');
      setThankDisabled(true);
      setTimeout(() => setThankDisabled(false), 60000);
    } catch (err) {
      console.error(err);
      showCopyMessage('ありがとう送信に失敗しました', true);
    }
  };

  // 公開リンク生成
  const handleGeneratePublicLink = async () => {
    if (!mindmap) return;
    try {
      const res = await axios.post(
        `${SERVER_URL}/api/mindmaps/${mindmap.id}/public`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      const fullUrl = window.location.origin + res.data.url;
      await navigator.clipboard.writeText(fullUrl);
      showCopyMessage('公開リンクをコピーしました！');
    } catch (err) {
      console.error(err);
      showCopyMessage('公開リンクの生成に失敗しました', true);
    }
  };

  // シェア（従来版）
  const handleShare = () => {
    const shareUrl = window.location.href;
    navigator.clipboard.writeText(shareUrl).then(() => {
      showCopyMessage('リンクをコピーしました');
    });
  };

  // ---------------------------------------
  // UI Helpers
  // ---------------------------------------
  const handleGoHome = () => {
    navigate('/mypage');
  };
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  const showCopyMessage = (msg) => {
    setCopyMessage(msg);
    setTimeout(() => setCopyMessage(''), 1500);
  };
  const showSaveMessage = (msg, isError = false) => {
    setSaveMessage(msg);
    setTimeout(() => setSaveMessage(''), 2000);
  };

  // タイトル編集
  const handleTitleDoubleClick = () => setIsEditingTitle(true);
  const handleTitleBlur = () => setIsEditingTitle(false);
  const handleTitleKeyDown = (e) => {
    if (e.key === 'Enter') setIsEditingTitle(false);
  };

  // エラー or ローディング
  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h1>エラー</h1>
        <p>{error}</p>
        <button onClick={fetchMindmap}>再取得</button>
      </div>
    );
  }
  if (!mindmap) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  // ---------------------------------------
  // Render
  // ---------------------------------------
  return (
    <div style={styles.pageContainer}>
      {/* ヘッダー */}
      <header style={styles.header}>
        <button onClick={handleGoHome} style={styles.homeButton}>
          ←
        </button>

        {isEditingTitle ? (
          <input
            ref={titleInputRef}
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            onBlur={handleTitleBlur}
            onKeyDown={handleTitleKeyDown}
            style={styles.titleInput}
          />
        ) : (
          <h2 style={styles.mapTitle} onDoubleClick={handleTitleDoubleClick} title="ダブルクリックでタイトル編集">
            {title}
          </h2>
        )}

        <button onClick={handleGeneratePublicLink} style={styles.publicLinkButton}>
          公開リンク
        </button>
        <button onClick={handleLogout} style={styles.logoutButton}>
          ログアウト
        </button>
      </header>

      {/* コピー／保存メッセージ */}
      {copyMessage && <div style={styles.messageBox}>{copyMessage}</div>}
      {saveMessage && <div style={styles.messageBox}>{saveMessage}</div>}

      <main style={styles.mainArea}>
        {/* ありがとうカウント */}
        <div style={styles.thankRow}>
          <div style={styles.thankCount}>{thankCount}</div>
          <button
            onClick={handleSendThank}
            disabled={thankDisabled}
            style={{
              ...styles.thankButton,
              background: thankDisabled ? '#ddd' : '#fafafa'
            }}
            title="ありがとうを送る（1分に1回まで）"
          >
            ↑
          </button>
          <small style={styles.titleNote}>※タイトルをダブルクリックで編集</small>
        </div>

        {/* キャンバス */}
        <div style={styles.canvasArea}>
          <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
        </div>

        {/* 操作用ボタン */}
        <div style={styles.buttonRow}>
          <button onClick={handleSave} style={styles.saveButton}>
            保存
          </button>
          <button onClick={() => navigate(`/export/${mindmap.id}`)} style={styles.exportButton}>
            Export Markdown
          </button>
        </div>
      </main>

      <footer style={styles.footer}>
        <small style={{ color: '#ddd' }}>&copy; 2025 MindMap App. All rights reserved.</small>
      </footer>
    </div>
  );
}

