import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const navigate = useNavigate();

  // サーバーからマインドマップ取得
  const fetchMindmap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`);
      setMindmap(res.data);
      setTitle(res.data.title);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    if (mindmapId) {
      fetchMindmap();
    }
  }, [mindmapId]);

  // 保存処理：nodes と edges を含む
  const handleSave = async () => {
    if (!mindmap) return;
    try {
      await axios.put(`${SERVER_URL}/api/mindmaps/${mindmap.id}`, {
        title,
        nodes: mindmap.nodes,
        edges: mindmap.edges
      });
      alert('保存しました！');
    } catch (err) {
      console.error(err);
    }
  };

  if (!mindmap) {
    return <div>読み込み中...</div>;
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>マインドマップ編集</h1>
      <div style={{ marginBottom: 10 }}>
        <label>タイトル: </label>
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ width: 200 }}
        />
      </div>
      <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
      <div style={{ marginTop: 20 }}>
        <button onClick={handleSave}>保存</button>
        {/* Export Markdown ページへ遷移するボタン */}
        <button
          onClick={() => navigate(`/export/${mindmap.id}`)}
          style={{ marginLeft: 10 }}
        >
          Export Markdown
        </button>
      </div>
    </div>
  );
}

export default Editor;
