// client/src/pages/ExportMarkdown.js
import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import { marked } from 'marked';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';
const MAX_DEPTH = 7; // 深さ（7階層）を超えたら無視

function ExportMarkdown() {
  const { mindmapId } = useParams();
  const navigate = useNavigate();

  const [mindmap, setMindmap] = useState(null);
  const [markdownText, setMarkdownText] = useState('');
  const [copyMode, setCopyMode] = useState(false);
  const [error, setError] = useState(null);

  useEffect(() => {
    if (mindmapId) {
      fetchData();
    }
    // eslint-disable-next-line
  }, [mindmapId]);

  // マインドマップ取得
  const fetchData = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`);
      setMindmap(res.data);
      setError(null);
    } catch (err) {
      console.error(err);
      setError('マインドマップを取得できませんでした');
    }
  };

  // マインドマップ取得後に番号付け＆Markdown生成
  useEffect(() => {
    if (mindmap) {
      const numberedNodes = buildNumberedTree(mindmap);
      const md = convertToMarkdown(numberedNodes);
      setMarkdownText(md);
    }
  }, [mindmap]);

  // ルートノード検出 → 再帰的に番号付け
  const buildNumberedTree = (mapData) => {
    const { nodes, edges } = mapData;
    if (!nodes || nodes.length === 0) return [];

    // ノードID -> インバウンド数
    const inboundCountMap = {};
    nodes.forEach((node) => {
      inboundCountMap[node.nodeId] = 0;
    });
    edges?.forEach((edge) => {
      if (inboundCountMap[edge.to] != null) {
        inboundCountMap[edge.to]++;
      }
    });

    // インバウンドが0のノード → ルート候補
    const rootNodes = nodes.filter((n) => inboundCountMap[n.nodeId] === 0);
    const rootNode = rootNodes[0] || nodes[0];
    if (!rootNode) return [];

    // ノードID -> ノード
    const nodeMap = {};
    nodes.forEach((nd) => {
      nodeMap[nd.nodeId] = nd;
    });

    // 親 -> 子ノードIDのマップ
    const childrenMap = {};
    nodes.forEach((nd) => {
      childrenMap[nd.nodeId] = [];
    });
    edges?.forEach((edge) => {
      if (childrenMap[edge.from]) {
        childrenMap[edge.from].push(edge.to);
      }
    });
    // 作成順にソート
    Object.keys(childrenMap).forEach((key) => {
      childrenMap[key].sort((a, b) => {
        const idxA = nodes.findIndex((n) => n.nodeId === a);
        const idxB = nodes.findIndex((n) => n.nodeId === b);
        return idxA - idxB;
      });
    });

    // 再帰で番号振り
    const result = [];
    const dfs = (nodeId, numbering) => {
      const depth = numbering.split('.').length;
      if (depth > MAX_DEPTH) return;
      const nodeObj = nodeMap[nodeId];
      if (!nodeObj) return;

      result.push({
        numbering,
        text: nodeObj.text || ''
      });

      const childs = childrenMap[nodeId] || [];
      childs.forEach((childId, index) => {
        const newNumber = `${numbering}.${index + 1}`;
        dfs(childId, newNumber);
      });
    };
    dfs(rootNode.nodeId, '1');
    return result;
  };

  // 番号付きツリーを Markdown文字列に
  const convertToMarkdown = (numberedNodes) => {
    let md = '';
    numberedNodes.forEach((item) => {
      const level = item.numbering.split('.').length;
      md += `${'#'.repeat(level)} ${item.numbering}\n`;
      md += `${item.text}\n\n`;
    });
    return md.trim();
  };

  // セクション単位でコピーモードを実装
  const handleCopySection = (sectionText) => {
    navigator.clipboard.writeText(sectionText).then(() => {
      alert('クリップボードにコピーしました');
    });
  };

  // Markdownをセクションごとに分割して表示
  const renderMarkdownWithCopy = (md) => {
    const lines = md.split('\n');
    const sections = [];
    let currentSection = [];

    lines.forEach((line) => {
      if (line.startsWith('#')) {
        if (currentSection.length > 0) {
          sections.push(currentSection);
        }
        currentSection = [line];
      } else {
        currentSection.push(line);
      }
    });
    if (currentSection.length > 0) {
      sections.push(currentSection);
    }

    return sections.map((sectionLines, idx) => {
      const sectionText = sectionLines.join('\n');
      // 非コピーモード
      if (!copyMode) {
        return (
          <div
            key={idx}
            style={{ marginBottom: '1em' }}
            dangerouslySetInnerHTML={{ __html: marked.parse(sectionText, { breaks: true }) }}
          />
        );
      }
      // コピーモード
      return (
        <div
          key={idx}
          style={{ cursor: 'pointer', marginBottom: '1em' }}
          onMouseOver={(e) => {
            e.currentTarget.style.backgroundColor = '#ffffa0';
          }}
          onMouseOut={(e) => {
            e.currentTarget.style.backgroundColor = 'transparent';
          }}
          onClick={() => handleCopySection(sectionText)}
          dangerouslySetInnerHTML={{ __html: marked.parse(sectionText, { breaks: true }) }}
        />
      );
    });
  };

  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h1>エラー</h1>
        <p>{error}</p>
        <button onClick={fetchData}>再取得</button>
      </div>
    );
  }

  if (!mindmap) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {/* 固定ヘッダー */}
      <header
        style={{
          position: 'relative',
          background: '#f5f5f5',
          borderBottom: '1px solid #ccc',
          padding: '10px 20px',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between'
        }}
      >
        {/* 左側：ホーム／Editor 戻るボタン */}
        <div style={{ display: 'flex', alignItems: 'center', gap: '10px' }}>
          <button
            onClick={() => navigate('/mypage')}
            style={{
              background: '#007BFF',
              color: '#fff',
              border: 'none',
              padding: '8px 12px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            ホームへ
          </button>
          <button
            onClick={() => navigate(`/editor/${mindmapId}`)}
            style={{
              background: '#6c757d',
              color: '#fff',
              border: 'none',
              padding: '8px 12px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Editorへ
          </button>
        </div>

        {/* 中央：マップタイトル */}
        <h3 style={{ margin: 0, fontSize: '20px' }}>{mindmap.title} - Export</h3>

        {/* 右側：コピーモード切替ボタン */}
        <button
          onClick={() => setCopyMode(!copyMode)}
          style={{
            background: copyMode ? '#dc3545' : '#28a745',
            color: '#fff',
            border: 'none',
            padding: '8px 16px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          {copyMode ? 'コピーモードOFF' : 'コピーモードON'}
        </button>
      </header>

      {/* メインコンテンツ: Markdown表示 */}
      <main style={{ flexGrow: 1, overflowY: 'auto', padding: '20px' }}>
        {renderMarkdownWithCopy(markdownText)}
      </main>

      {/* フッター */}
      <footer
        style={{
          background: '#f5f5f5',
          borderTop: '1px solid #ccc',
          textAlign: 'center',
          padding: '10px 20px'
        }}
      >
        <small style={{ color: '#888' }}>
          &copy; 2025 MindMap App. All rights reserved.
        </small>
      </footer>
    </div>
  );
}

export default ExportMarkdown;
