import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useParams } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const [arrangementMode, setArrangementMode] = useState('manual');

  // サーバーからマインドマップ取得
  const fetchMindmap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`);
      const data = res.data;
      // lines プロパティがなければ初期化
      if (!data.lines) data.lines = [];
      setMindmap(data);
      setTitle(data.title);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    if (mindmapId) {
      fetchMindmap();
    }
  }, [mindmapId]);

  // 保存処理：ノードと線をサーバーに送信
  const handleSave = async () => {
    if (!mindmap) return;
    try {
      await axios.put(`${SERVER_URL}/api/mindmaps/${mindmap.id}`, {
        title,
        nodes: mindmap.nodes,
        lines: mindmap.lines
      });
      alert('保存しました！');
    } catch (err) {
      console.error(err);
    }
  };

  // 自動配置処理：中心ノード（最初のノード）を軸に、子ノードを円状に配置
  const autoArrangeNodes = (mode) => {
    if (!mindmap || mindmap.nodes.length === 0) return;
    const center = mindmap.nodes[0];
    const children = mindmap.nodes.slice(1);
    const count = children.length;
    if (count === 0) return;
    const radius = 200;

    let sortedChildren = [...children];
    if (mode === 'new') {
      sortedChildren.sort((a, b) => new Date(a.createdAt) - new Date(b.createdAt));
    } else if (mode === 'popular') {
      sortedChildren.sort((a, b) => (b.popularity || 0) - (a.popularity || 0));
    } else if (mode === 'relevance') {
      sortedChildren.sort((a, b) => (b.relevance || 0) - (a.relevance || 0));
    }

    const angleStep = (2 * Math.PI) / count;
    const arrangedChildren = sortedChildren.map((node, index) => {
      const angle = index * angleStep;
      return {
        ...node,
        x: center.x + radius * Math.cos(angle),
        y: center.y + radius * Math.sin(angle)
      };
    });

    const newNodes = [center, ...arrangedChildren];
    setMindmap({ ...mindmap, nodes: newNodes });
  };

  // ノード追加機能
  const handleAddNode = () => {
    if (!mindmap) return;
    const newNode = {
      nodeId: `node_${Date.now()}`,
      text: '新しいノード',
      x: 100,
      y: 100,
      createdAt: new Date().toISOString()
    };
    const newNodes = [...mindmap.nodes, newNode];
    setMindmap({ ...mindmap, nodes: newNodes });
  };

  // 線追加機能：ここではシンプルに最後の2ノード間に線を追加
  const handleAddLine = () => {
    if (!mindmap || mindmap.nodes.length < 2) {
      alert('線を引くには少なくとも2つのノードが必要です');
      return;
    }
    const nodes = mindmap.nodes;
    const fromNode = nodes[nodes.length - 2];
    const toNode = nodes[nodes.length - 1];
    const newLine = {
      lineId: `line_${Date.now()}`,
      from: fromNode.nodeId,
      to: toNode.nodeId
    };
    const newLines = [...(mindmap.lines || []), newLine];
    setMindmap({ ...mindmap, lines: newLines });
  };

  if (!mindmap) {
    return <div>読み込み中...</div>;
  }

  return (
    <div style={{ padding: 20 }}>
      <h1>マインドマップ編集</h1>
      <div style={{ marginBottom: 10 }}>
        <label>タイトル: </label>
        <input
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ width: 200 }}
        />
      </div>
      <div style={{ marginBottom: 10 }}>
        <label>自動配置モード: </label>
        <select
          value={arrangementMode}
          onChange={(e) => setArrangementMode(e.target.value)}
        >
          <option value="manual">手動</option>
          <option value="popular">人気順</option>
          <option value="new">新着順</option>
          <option value="relevance">関連性</option>
        </select>
        {arrangementMode !== 'manual' && (
          <button
            onClick={() => autoArrangeNodes(arrangementMode)}
            style={{ marginLeft: 10 }}
          >
            自動配置実行
          </button>
        )}
      </div>
      <div style={{ marginBottom: 10 }}>
        <button onClick={handleAddNode}>ノード追加</button>
        <button onClick={handleAddLine} style={{ marginLeft: 10 }}>線追加</button>
      </div>
      <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
      <div style={{ marginTop: 20 }}>
        <button onClick={handleSave}>保存</button>
      </div>
    </div>
  );
}

export default Editor;
