// client/src/pages/MyPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function MyPage() {
  const navigate = useNavigate();
  const [mindmaps, setMindmaps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ログイントークン
  const token = localStorage.getItem('token');

  useEffect(() => {
    // トークンがなければログインへ
    if (!token) {
      alert('ログインが必要です');
      navigate('/login');
      return;
    }
    fetchMyMindmaps();
    // eslint-disable-next-line
  }, []);

  // 自分のマインドマップ一覧取得
  const fetchMyMindmaps = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/my`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      setMindmaps(res.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.response ? err.response.data.error : err.message);
      setLoading(false);
    }
  };

  const handleGoEditor = (mapId) => {
    navigate(`/editor/${mapId}`);
  };

  if (loading) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }
  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h2>エラー</h2>
        <p>{error}</p>
        <button onClick={fetchMyMindmaps}>再読み込み</button>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>マイページ</h2>
      <p>あなたが所有しているマインドマップ一覧です。</p>
      {mindmaps.length === 0 ? (
        <p>マインドマップがありません。新規作成してみましょう。</p>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
            gap: 16
          }}
        >
          {mindmaps.map((map) => (
            <div
              key={map._id}
              style={{
                border: '1px solid #ccc',
                borderRadius: '4px',
                padding: 10,
                cursor: 'pointer'
              }}
              onClick={() => handleGoEditor(map.id)}
            >
              <h4 style={{ margin: '0 0 8px' }}>{map.title}</h4>
              <small style={{ color: '#666' }}>
                {new Date(map.createdAt).toLocaleString()}
              </small>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MyPage;
