// client/src/pages/PublicView.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function PublicView() {
  const { shareId } = useParams();
  const navigate = useNavigate();
  const [mapData, setMapData] = useState(null);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchPublicMap();
    // eslint-disable-next-line
  }, [shareId]);

  const fetchPublicMap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/public/${shareId}`);
      setMapData(res.data);
    } catch (err) {
      console.error(err);
      setError('このマップは見つかりませんか、もしくは非公開です。');
    }
  };

  // 複製ボタン → ログイン必須。未ログインなら /loginへ誘導
  const handleClone = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('ログインが必要です。ログイン後に再度お試しください。');
      navigate('/login');
      return;
    }
    try {
      const res = await axios.post(`${SERVER_URL}/api/mindmaps/public/${shareId}/clone`, {}, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // 複製が完了したマップへ遷移
      navigate(`/editor/${res.data.id}`);
    } catch (err) {
      console.error(err);
      alert('複製に失敗しました');
    }
  };

  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h2>エラー</h2>
        <p>{error}</p>
      </div>
    );
  }
  if (!mapData) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  return (
    <div style={{ padding: 20 }}>
      <h2>読み取り専用マップ</h2>
      <p>タイトル: {mapData.title}</p>

      {/* 簡易プレビュー（読み取り専用） */}
      <div style={{ border: '1px solid #ccc', padding: 10, marginBottom: 20 }}>
        {/* ここで mapData.nodes を描画するか、サマリ表示などお好みで */}
        <p>ノード数: {mapData.nodes.length}</p>
        <p>エッジ数: {mapData.edges.length}</p>
        <p>オーナー: (非表示かIDのみ表示するかは任意)</p>
      </div>

      <button onClick={handleClone}>このマップを複製して編集する</button>
    </div>
  );
}

export default PublicView;
