// client/src/pages/PublicView.js
import React, { useEffect, useState } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import axios from 'axios';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function PublicView() {
  const { shareId } = useParams();
  const navigate = useNavigate();

  const [mapData, setMapData] = useState(null);
  const [error, setError] = useState('');

  useEffect(() => {
    fetchPublicMap();
    // eslint-disable-next-line
  }, [shareId]);

  // 公開マップを読み取り
  const fetchPublicMap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/public/${shareId}`);
      setMapData(res.data);
    } catch (err) {
      console.error(err);
      setError('このマップは見つからないか、既に非公開になっています。');
    }
  };

  // 複製ボタン押下 → ログインチェック & 複製API呼び出し
  const handleClone = async () => {
    const token = localStorage.getItem('token');
    if (!token) {
      alert('マップを複製するにはログインが必要です。ログインページへ移動します。');
      navigate('/login');
      return;
    }
    try {
      const res = await axios.post(
        `${SERVER_URL}/api/mindmaps/public/${shareId}/clone`,
        {},
        {
          headers: { Authorization: `Bearer ${token}` }
        }
      );
      // 複製されたマップをEditorで開く
      navigate(`/editor/${res.data.id}`);
    } catch (err) {
      console.error(err);
      alert('マップの複製に失敗しました。');
    }
  };

  if (error) {
    return (
      <div style={styles.container}>
        <div style={styles.card}>
          <h2>エラー</h2>
          <p>{error}</p>
        </div>
      </div>
    );
  }

  if (!mapData) {
    return (
      <div style={styles.container}>
        <div style={styles.card}>読み込み中...</div>
      </div>
    );
  }

  return (
    <div style={styles.container}>
      {/* カード風の表示 */}
      <div style={styles.card}>
        <h2 style={{ marginTop: 0, marginBottom: '1rem' }}>読み取り専用マップ</h2>

        <div style={styles.mapInfo}>
          <p style={styles.title}>{mapData.title}</p>
          <p style={{ margin: '0.5rem 0' }}>ノード数: {mapData.nodes.length}</p>
          <p style={{ margin: '0.5rem 0' }}>エッジ数: {mapData.edges.length}</p>
        </div>

        {/* 複製ボタン */}
        <button onClick={handleClone} style={styles.cloneButton}>
          このマップを複製する
        </button>
      </div>
    </div>
  );
}

export default PublicView;

// シンプルなインラインCSS例
const styles = {
  container: {
    minHeight: '100vh',
    background: '#f9f9f9',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    padding: '20px'
  },
  card: {
    width: '400px',
    padding: '2rem',
    borderRadius: '6px',
    background: '#fff',
    boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
    textAlign: 'center'
  },
  mapInfo: {
    textAlign: 'left',
    marginBottom: '1.5rem',
    background: '#f2f2f2',
    padding: '1rem',
    borderRadius: '4px'
  },
  title: {
    fontSize: '18px',
    fontWeight: 'bold',
    margin: 0
  },
  cloneButton: {
    background: '#007BFF',
    color: '#fff',
    border: 'none',
    padding: '0.75rem 1.5rem',
    borderRadius: '4px',
    cursor: 'pointer',
    fontSize: '16px'
  }
};
