import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function MyPage() {
  const navigate = useNavigate();
  const [mindmaps, setMindmaps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // ログイン用トークンを localStorage から取得
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      alert('ログインが必要です');
      navigate('/login');
      return;
    }
    fetchMyMindmaps();
  }, [token, navigate]);

  const fetchMyMindmaps = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/my`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      setMindmaps(res.data);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError('マインドマップの取得に失敗しました');
      setLoading(false);
    }
  };

  const handleGoEditor = (mapId) => {
    navigate(`/editor/${mapId}`);
  };

  if (loading) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }
  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h2>エラー</h2>
        <p>{error}</p>
        <button onClick={fetchMyMindmaps}>再読み込み</button>
      </div>
    );
  }

  return (
    <div style={{ padding: 20 }}>
      {/* ヘッダー */}
      <header
        style={{
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'space-between',
          borderBottom: '1px solid #ccc',
          paddingBottom: '10px',
          marginBottom: '20px',
        }}
      >
        <h2 style={{ margin: 0 }}>マイページ</h2>
        <button
          onClick={() => navigate('/')}
          style={{
            background: '#007BFF',
            color: '#fff',
            border: 'none',
            padding: '8px 12px',
            borderRadius: '4px',
            cursor: 'pointer',
          }}
        >
          ホームへ
        </button>
      </header>

      <p>あなたが所有するマインドマップ一覧です。</p>

      {mindmaps.length === 0 ? (
        <p>マインドマップが見つかりません。新規作成してみましょう。</p>
      ) : (
        <div
          style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
            gap: '16px',
          }}
        >
          {mindmaps.map((map) => (
            <div
              key={map.id}
              onClick={() => handleGoEditor(map.id)}
              style={{
                border: '1px solid #ccc',
                borderRadius: '4px',
                padding: '10px',
                background: '#fff',
                cursor: 'pointer',
              }}
            >
              <h3 style={{ margin: '0 0 8px' }}>{map.title}</h3>
              <small style={{ color: '#666' }}>
                {new Date(map.createdAt).toLocaleString()}
              </small>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default MyPage;
