// client/src/pages/Editor.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';
import ThankButton from '../components/ThankButton';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  // スクロールコンテナの ref とスクロール位置
  const scrollContainerRef = useRef(null);
  const [hScroll, setHScroll] = useState(0);
  const [vScroll, setVScroll] = useState(0);

  useEffect(() => {
    if (mindmapId) {
      fetchMindmap();
    }
  }, [mindmapId]);

  const fetchMindmap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`);
      setMindmap(res.data);
      setTitle(res.data.title);
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.response ? err.response.data.error : err.message);
      setMindmap(null);
    }
  };

  const handleSave = async () => {
    if (!mindmap) return;
    try {
      await axios.put(`${SERVER_URL}/api/mindmaps/${mindmap.id}`, {
        title,
        nodes: mindmap.nodes,
        edges: mindmap.edges
      });
      alert('保存しました！');
    } catch (err) {
      console.error(err);
      alert('保存に失敗しました');
    }
  };

  // SNS共有: 現在の URL をコピー
  const handleShare = () => {
    const shareUrl = window.location.href;
    navigator.clipboard.writeText(shareUrl).then(() => {
      alert('リンクをコピーしました');
    });
  };

  // ホーム画面へ戻る
  const handleGoHome = () => {
    navigate('/');
  };

  // カスタムスクロールバー用のハンドラー
  const handleHorizontalScroll = (e) => {
    const value = e.target.value;
    setHScroll(value);
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollLeft = value;
    }
  };

  const handleVerticalScroll = (e) => {
    const value = e.target.value;
    setVScroll(value);
    if (scrollContainerRef.current) {
      scrollContainerRef.current.scrollTop = value;
    }
  };

  const onScroll = () => {
    if (scrollContainerRef.current) {
      setHScroll(scrollContainerRef.current.scrollLeft);
      setVScroll(scrollContainerRef.current.scrollTop);
    }
  };

  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h1>エラー</h1>
        <p>{error}</p>
        <button onClick={fetchMindmap}>再取得</button>
      </div>
    );
  }

  if (!mindmap) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  // 最大スクロール値（コンテナ内のコンテンツサイズに応じて）
  const maxH = scrollContainerRef.current ? scrollContainerRef.current.scrollWidth - scrollContainerRef.current.clientWidth : 0;
  const maxV = scrollContainerRef.current ? scrollContainerRef.current.scrollHeight - scrollContainerRef.current.clientHeight : 0;

  return (
    <div style={{ padding: 20 }}>
      {/* ヘッダー */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: 10 }}>
        {/* ホームボタン */}
        <button
          onClick={handleGoHome}
          style={{
            width: 40,
            height: 40,
            borderRadius: '50%',
            border: 'none',
            background: '#007BFF',
            color: '#fff',
            fontSize: '20px',
            marginRight: 10,
            cursor: 'pointer'
          }}
        >
          ←
        </button>
        <h2 style={{ flexGrow: 1, margin: 0 }}>{title}</h2>
        <button onClick={handleShare} style={{ marginLeft: 10 }}>
          SNSで共有
        </button>
      </div>

      {/* ありがとうボタン＆カウント（キャンバス外に配置） */}
      <div style={{ marginBottom: 10 }}>
        <ThankButton mindmapId={mindmap.id} />
      </div>

      {/* スクロール可能なキャンバスコンテナ */}
      <div
        style={{
          position: 'relative',
          border: '1px solid #ccc',
          width: 800,
          height: 500,
          overflow: 'auto'
        }}
        ref={scrollContainerRef}
        onScroll={onScroll}
      >
        <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
      </div>

      {/* カスタム上部（水平）スクロールバー */}
      <div style={{ marginTop: 5, width: 800 }}>
        <input
          type="range"
          min={0}
          max={maxH}
          value={hScroll}
          onChange={handleHorizontalScroll}
          style={{ width: '100%' }}
        />
      </div>

      {/* カスタム左側（垂直）スクロールバー（ここでは簡易的に下部に水平バーとして実装） */}
      <div style={{ marginTop: 5, width: 800 }}>
        <input
          type="range"
          min={0}
          max={maxV}
          value={vScroll}
          onChange={handleVerticalScroll}
          style={{ width: '100%' }}
        />
      </div>

      {/* 保存＆Export Markdown ボタン */}
      <div style={{ marginTop: 20 }}>
        <button onClick={handleSave}>保存</button>
        <button onClick={() => navigate(`/export/${mindmap.id}`)} style={{ marginLeft: 10 }}>
          Export Markdown
        </button>
      </div>
    </div>
  );
}

export default Editor;
