// client/src/pages/Editor.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const navigate = useNavigate();

  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const [error, setError] = useState(null);
  const [isEditingTitle, setIsEditingTitle] = useState(false);
  const titleInputRef = useRef(null);

  // ありがとうカウント
  const [thankCount, setThankCount] = useState(0);
  const [thankDisabled, setThankDisabled] = useState(false);

  // メッセージ用 state
  const [copyMessage, setCopyMessage] = useState('');
  const [saveMessage, setSaveMessage] = useState('');

  // ログイン用トークン
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      alert('ログインが必要です');
      navigate('/login');
      return;
    }
    if (mindmapId) {
      fetchMindmap();
      fetchThankCount();
    }
    // eslint-disable-next-line
  }, [mindmapId]);

  const fetchMindmap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMindmap(res.data);
      setTitle(res.data.title);
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.response ? err.response.data.error : err.message);
      setMindmap(null);
    }
  };

  const fetchThankCount = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/thank/${mindmapId}`);
      setThankCount(res.data.count);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSave = async () => {
    if (!mindmap) return;
    try {
      await axios.put(
        `${SERVER_URL}/api/mindmaps/${mindmap.id}`,
        { title, nodes: mindmap.nodes, edges: mindmap.edges },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      showSaveMessage('保存しました！');
    } catch (err) {
      console.error(err);
      if (err.response && err.response.status === 403) {
        showSaveMessage('他人のマップです。編集権限がありません', true);
      } else {
        showSaveMessage('保存に失敗しました', true);
      }
    }
  };

  const handleSendThank = async () => {
    if (thankDisabled) return;
    try {
      await axios.post(`${SERVER_URL}/api/thank`, { mindmapId });
      await fetchThankCount();
      showCopyMessage('ありがとうを送りました！');
      setThankDisabled(true);
      setTimeout(() => {
        setThankDisabled(false);
      }, 60000);
    } catch (err) {
      console.error(err);
      showCopyMessage('ありがとう送信に失敗しました', true);
    }
  };

  const handleShare = () => {
    const shareUrl = window.location.href;
    navigator.clipboard.writeText(shareUrl).then(() => {
      showCopyMessage('リンクをコピーしました');
    });
  };

  const handleGoHome = () => {
    navigate('/mypage');
  };

  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // メッセージ表示関数（コピー用）
  const showCopyMessage = (msg) => {
    setCopyMessage(msg);
    setTimeout(() => {
      setCopyMessage('');
    }, 1500);
  };

  const handleGeneratePublicLink = async () => {
    if (!mindmap) return;
    try {
      const token = localStorage.getItem('token');
      const res = await axios.post(
        `${SERVER_URL}/api/mindmaps/${mindmap.id}/public`,
        {},
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // res.data.url で受け取った公開URLをコピー
      const fullUrl = window.location.origin + res.data.url; 
      await navigator.clipboard.writeText(fullUrl);
      showCopyMessage('公開リンクをコピーしました！');
    } catch (err) {
      console.error(err);
      showCopyMessage('公開リンクの生成に失敗しました', true);
    }
  };

  // 保存用メッセージ表示関数
  const showSaveMessage = (msg, isError = false) => {
    setSaveMessage(msg);
    setTimeout(() => {
      setSaveMessage('');
    }, 2000);
  };

  // タイトル編集
  const handleTitleDoubleClick = () => setIsEditingTitle(true);
  const handleTitleBlur = () => setIsEditingTitle(false);
  const handleTitleKeyDown = (e) => {
    if (e.key === 'Enter') setIsEditingTitle(false);
  };

  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h1>エラー</h1>
        <p>{error}</p>
        <button onClick={fetchMindmap}>再取得</button>
      </div>
    );
  }
  if (!mindmap) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  return (
    <div style={{ display: 'flex', flexDirection: 'column', minHeight: '100vh' }}>
      {/* ヘッダー */}
      <header
        style={{
          padding: '10px 20px',
          background: '#f5f5f5',
          borderBottom: '1px solid #ccc',
          display: 'flex',
          alignItems: 'center'
        }}
      >
        <button
          onClick={handleGoHome}
          style={{
            width: 40,
            height: 40,
            borderRadius: '50%',
            border: 'none',
            background: '#007BFF',
            color: '#fff',
            fontSize: '20px',
            marginRight: 10,
            cursor: 'pointer'
          }}
        >
          ←
        </button>

        {isEditingTitle ? (
          <input
            ref={titleInputRef}
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            onBlur={handleTitleBlur}
            onKeyDown={handleTitleKeyDown}
            style={{
              fontSize: '24px',
              flexGrow: 1,
              border: '1px solid #ccc',
              borderRadius: '4px',
              padding: '4px 8px'
            }}
          />
        ) : (
          <h2
            onDoubleClick={handleTitleDoubleClick}
            style={{ flexGrow: 1, margin: 0, fontSize: '24px', cursor: 'text' }}
            title="ダブルクリックでタイトルを編集できます"
          >
            {title}
          </h2>
        )}

        <button
          onClick={handleGeneratePublicLink}
          style={{
            marginLeft: 10,
            border: 'none',
            background: '#28a745',
            color: '#fff',
            padding: '8px 12px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          リンクを共有
        </button>

        <button
          onClick={handleLogout}
          style={{
            marginLeft: 10,
            border: 'none',
            background: '#dc3545',
            color: '#fff',
            padding: '8px 12px',
            borderRadius: '4px',
            cursor: 'pointer'
          }}
        >
          ログアウト
        </button>
      </header>

      {/* コピーなどのメッセージは上部に表示 */}
      {copyMessage && (
        <div
          style={{
            background: '#c3e6cb',
            color: '#155724',
            padding: '10px',
            margin: '10px 20px',
            borderRadius: '4px'
          }}
        >
          {copyMessage}
        </div>
      )}

      <main style={{ padding: '20px', flexGrow: 1 }}>
        <div style={{ display: 'flex', alignItems: 'center', marginBottom: 16 }}>
          <div style={{ fontSize: '32px', fontWeight: 'bold', marginRight: 10 }}>
            {thankCount}
          </div>
          <button
            onClick={handleSendThank}
            disabled={thankDisabled}
            style={{
              border: '1px solid #ccc',
              background: thankDisabled ? '#e0e0e0' : '#f0f0f0',
              padding: '8px 12px',
              cursor: thankDisabled ? 'not-allowed' : 'pointer',
              borderRadius: '4px'
            }}
            title="ありがとうを送る（1分間に一度のみ送れます）"
          >
            ↑
          </button>
          <div style={{ marginLeft: 50 }}>
            <small style={{ color: '#666' }}>
              ※タイトルをダブルクリックで編集できます
            </small>
          </div>
        </div>

        {/* キャンバス */}
        <div
          style={{
            position: 'relative',
            border: '1px solid #ccc',
            width: '100%',
            maxWidth: '1000px',
            height: '545px',
            //overflow: 'auto',
            marginBottom: 20
          }}
        >
          <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
        </div>

        {/* 保存メッセージをキャンバス下・保存ボタン上に表示 */}
        {saveMessage && (
          <div style={{
            background: '#c3e6cb',
            color: '#155724',
            padding: '10px',
            margin: '10px 20px',
            borderRadius: '4px'
          }}>
            {saveMessage}
          </div>
        )}

        {/* 操作用ボタン */}
        <div style={{ display: 'flex', gap: '10px' }}>
          <button
            onClick={handleSave}
            style={{
              background: '#007BFF',
              color: '#fff',
              border: 'none',
              padding: '10px 16px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            保存
          </button>
          <button
            onClick={() => navigate(`/export/${mindmap.id}`)}
            style={{
              background: '#6c757d',
              color: '#fff',
              border: 'none',
              padding: '10px 16px',
              borderRadius: '4px',
              cursor: 'pointer'
            }}
          >
            Export Markdown
          </button>
        </div>
      </main>

      <footer
        style={{
          background: '#f5f5f5',
          borderTop: '1px solid #ccc',
          padding: '10px 20px',
          textAlign: 'center'
        }}
      >
        <small style={{ color: '#888' }}>
          &copy; 2025 MindMap App. All rights reserved.
        </small>
      </footer>
    </div>
  );
}

export default Editor;
