import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';
import MindmapCard from '../components/MindmapCard';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Home() {
  const [mindmaps, setMindmaps] = useState([]);
  const navigate = useNavigate();

  const fetchMindmaps = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps`);
      setMindmaps(res.data);
    } catch (err) {
      console.error(err);
    }
  };

  useEffect(() => {
    fetchMindmaps();
  }, []);

  // 新規マインドマップ作成 (簡易版)
  const handleCreateMindmap = async () => {
    const newId = `mindmap_${Date.now()}`;
    const payload = {
      id: newId,
      title: '新しいマップ',
      nodes: [
        {
          nodeId: `node_${Date.now()}`,
          text: 'テーマ',
          x: 300,
          y: 150
        }
      ]
    };
    try {
      const res = await axios.post(`${SERVER_URL}/api/mindmaps`, payload);
      navigate(`/editor/${res.data.id}`);
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div style={{ padding: 20 }}>
      <h1>ホーム画面</h1>
      <button onClick={handleCreateMindmap}>新しいマインドマップを作成</button>
      <div style={{ marginTop: 20 }}>
        {mindmaps.map((map) => (
          <MindmapCard key={map.id} mindmap={map} />
        ))}
      </div>
    </div>
  );
}

export default Home;
