// client/src/pages/MyPage.js
import React, { useEffect, useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function MyPage() {
  const navigate = useNavigate();
  const [mindmaps, setMindmaps] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  // JWT トークンを localStorage から取得
  const token = localStorage.getItem('token');

  useEffect(() => {
    if (!token) {
      alert('ログインが必要です');
      navigate('/login');
      return;
    }
    fetchMyMindmaps();
    // eslint-disable-next-line
  }, [token]);

  // -----------------------------------------------
  // API: 自分のマインドマップ一覧取得
  // -----------------------------------------------
  const fetchMyMindmaps = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/my`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      // favoriteフラグを付与 (バックエンドが対応していればそのまま)
      const dataWithFav = res.data.map((m) => ({ ...m, favorite: m.favorite === true }));
      setMindmaps(dataWithFav);
      setLoading(false);
    } catch (err) {
      console.error(err);
      setError(err.response ? err.response.data.error : 'エラーが発生しました');
      setLoading(false);
    }
  };

  // -----------------------------------------------
  // 新規マップ作成 → Editorへ遷移
  // -----------------------------------------------
  const handleCreateNew = async () => {
    try {
      const newId = `mindmap_${Date.now()}`;
      const payload = {
        id: newId,
        title: '新しいマップ',
        nodes: [
          {
            nodeId: `node_${Date.now()}`,
            text: 'テーマ',
            x: 300,
            y: 150
          }
        ]
      };
      const res = await axios.post(`${SERVER_URL}/api/mindmaps`, payload, {
        headers: { Authorization: `Bearer ${token}` }
      });
      navigate(`/editor/${res.data.id}`);
    } catch (err) {
      console.error(err);
      alert('マインドマップの作成に失敗しました');
    }
  };

  // -----------------------------------------------
  // マップ削除
  // -----------------------------------------------
  const handleDelete = async (mapId) => {
    if (!window.confirm('本当に削除しますか？')) return;
    try {
      await axios.delete(`${SERVER_URL}/api/mindmaps/${mapId}`, {
        headers: { Authorization: `Bearer ${token}` }
      });
      setMindmaps((prev) => prev.filter((m) => m.id !== mapId));
    } catch (err) {
      console.error(err);
      if (err.response) {
        if (err.response.status === 403) {
          alert('削除に失敗しました: 他人のマップです。編集権限がありません。');
        } else {
          alert(`削除に失敗しました: ${err.response.data.error || '不明なエラー'}`);
        }
      } else {
        alert('削除に失敗しました');
      }
    }
  };

  // -----------------------------------------------
  // お気に入りトグル
  // -----------------------------------------------
  const toggleFavorite = async (mapId, currentFav) => {
    try {
      const res = await axios.put(
        `${SERVER_URL}/api/mindmaps/${mapId}/favorite`,
        { favorite: !currentFav },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      // 結果をstateに反映
      setMindmaps((prev) =>
        prev.map((m) => (m.id === mapId ? { ...m, favorite: res.data.favorite } : m))
      );
    } catch (err) {
      console.error(err);
      alert('お気に入りの更新に失敗しました');
    }
  };

  // -----------------------------------------------
  // Editorへ
  // -----------------------------------------------
  const handleGoEditor = (mapId) => {
    navigate(`/editor/${mapId}`);
  };

  // ログアウト
  const handleLogout = () => {
    localStorage.removeItem('token');
    navigate('/login');
  };

  // -----------------------------------------------
  // Render
  // -----------------------------------------------
  if (loading) {
    return <div style={styles.loading}>読み込み中...</div>;
  }
  if (error) {
    return (
      <div style={styles.loading}>
        <h2>エラー</h2>
        <p>{error}</p>
        <button style={styles.reloadButton} onClick={fetchMyMindmaps}>
          再読み込み
        </button>
      </div>
    );
  }

  return (
    <div style={styles.pageContainer}>
      {/* ヘッダー */}
      <header style={styles.header}>
        <h1 style={styles.headerTitle}>My Mindmaps</h1>
        <div style={{ display: 'flex', gap: '10px' }}>
          <button style={styles.createButton} onClick={handleCreateNew}>
            新規マインドマップ作成
          </button>
          <button style={styles.logoutButton} onClick={handleLogout}>
            ログアウト
          </button>
        </div>
      </header>

      {/* メインコンテンツ */}
      <main style={styles.mainArea}>
        {mindmaps.length === 0 ? (
          <p style={styles.noMapsText}>
            マインドマップがありません。上の「新規マインドマップ作成」ボタンから作成してください。
          </p>
        ) : (
          <div style={styles.gridWrapper}>
            {mindmaps.map((map) => (
              <div key={map.id} style={styles.mapCard}>
                {/* お気に入りスター: 右上 */}
                <div
                  onClick={() => toggleFavorite(map.id, map.favorite)}
                  style={{
                    ...styles.starIcon,
                    color: map.favorite ? 'gold' : '#ccc'
                  }}
                  title="お気に入りに登録 / 解除"
                >
                  {map.favorite ? '★' : '☆'}
                </div>

                <h3 style={styles.mapTitle} onClick={() => handleGoEditor(map.id)}>
                  {map.title}
                </h3>
                <small style={styles.mapTime}>
                  {new Date(map.createdAt).toLocaleString()}
                </small>

                <button
                  onClick={(e) => {
                    e.stopPropagation();
                    handleDelete(map.id);
                  }}
                  style={styles.deleteButton}
                  onMouseOver={(e) => (e.currentTarget.style.backgroundColor = '#9c342d')}
                  onMouseOut={(e) => (e.currentTarget.style.backgroundColor = '#bb4a43')}
                  title="このマップを削除します"
                >
                  削除
                </button>
              </div>
            ))}
          </div>
        )}
      </main>

      {/* フッター */}
      <footer style={styles.footer}>
        <small style={{ color: '#d2c8bc' }}>&copy; 2025 MindMap App. All rights reserved.</small>
      </footer>
    </div>
  );
}

export default MyPage;

// スタイル定義：地下本棚の雰囲気
const styles = {
  pageContainer: {
    minHeight: '100vh',
    background: '#2e2a25', // ダークブラウン系。地下の薄暗い本棚イメージ
    display: 'flex',
    flexDirection: 'column'
  },
  header: {
    background: '#3e372f', // 少し明るいブラウン
    borderBottom: '2px solid #2b2723',
    padding: '10px 20px',
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center'
  },
  headerTitle: {
    margin: 0,
    color: '#e3d9cc', // 薄いベージュ系フォント
    fontFamily: 'Georgia, serif'
  },
  createButton: {
    background: '#7b4b28', // 落ち着いた茶系
    color: '#fff',
    border: 'none',
    padding: '8px 16px',
    borderRadius: '4px',
    cursor: 'pointer',
    fontFamily: 'inherit'
  },
  logoutButton: {
    background: '#bb4a43',
    color: '#fff',
    border: 'none',
    padding: '8px 12px',
    borderRadius: '4px',
    cursor: 'pointer',
    fontFamily: 'inherit'
  },
  mainArea: {
    flexGrow: 1,
    padding: '20px'
  },
  noMapsText: {
    color: '#e3d9cc'
  },
  gridWrapper: {
    display: 'grid',
    gridTemplateColumns: 'repeat(auto-fill, minmax(250px, 1fr))',
    gap: '16px'
  },
  mapCard: {
    position: 'relative',
    border: '1px solid #2b2723',
    borderRadius: '4px',
    background: '#4a423a', // さらに暗い茶色
    padding: '10px',
    boxShadow: '0 2px 4px rgba(0,0,0,0.5)',
    color: '#f0ece9', // カード内テキストを薄い色に
    fontFamily: 'Georgia, serif'
  },
  starIcon: {
    position: 'absolute',
    top: 8,
    right: 8,
    fontSize: '20px',
    cursor: 'pointer'
  },
  mapTitle: {
    margin: '0 0 8px',
    cursor: 'pointer',
    fontSize: '18px',
    color: '#f0ece9'
  },
  mapTime: {
    color: '#d2c8bc'
  },
  deleteButton: {
    marginTop: '10px',
    background: '#bb4a43',
    color: '#fff',
    border: 'none',
    padding: '6px 12px',
    borderRadius: '4px',
    cursor: 'pointer',
    transition: 'background 0.3s',
    fontFamily: 'inherit'
  },
  footer: {
