import React, { useState, useRef } from 'react';
import { marked } from 'marked';
import ThankButton from './ThankButton';

/**
 * MindmapCanvas.js
 * 
 * キャンバス上でノードを自由配置・編集するコンポーネント
 * 
 * 追加機能:
 * - ノード追加ボタン
 * - エッジ追加モード（既存機能）
 * - ゴミ箱アイコンによるドラッグ＆ドロップでの削除機能
 *   - キャンバス下部中央に赤いゴミ箱アイコンを絶対配置（スクロール影響なし）
 *   - ドラッグ中のノードは通常より大きく表示、ゴミ箱上に来ると元サイズになり、ゴミ箱アイコンが大きく表示される
 * - ノードはダブルクリックで Markdown 形式のテキスト編集モードに入り、
 *   textarea により複数行入力が可能（Shift+Enter で改行）、Enter キー単体で編集完了し Markdown でレンダリング
 * - ノードのサイズはユーザーがリサイズ可能（resize: both）、はみ出た部分は隠れる
 * - 編集モード中は scale は常に 1.0 とし、編集終了時に現在の scale 分の拡大分を打ち消して実サイズとして保存する
 */

const DEFAULT_NODE_WIDTH = 150;
const DEFAULT_NODE_HEIGHT = 80;

function MindmapCanvas({ mindmap, setMindmap }) {
  const [dragging, setDragging] = useState({
    isDragging: false,
    nodeIndex: null,
    offsetX: 0,
    offsetY: 0
  });
  const [isEdgeMode, setIsEdgeMode] = useState(false);
  const [edgeStart, setEdgeStart] = useState(null);
  const [editingNodeId, setEditingNodeId] = useState(null);
  const [editingText, setEditingText] = useState('');
  const [trashHovered, setTrashHovered] = useState(false);
  const trashRef = useRef(null);

  // ノードのサイズ更新（resize 操作時）：
  // 第3引数 scaleFactor で実際の拡大分を打ち消して実サイズを計算
  const handleNodeResize = (index, el, scaleFactor = 1.0) => {
    const newWidth = el.offsetWidth / scaleFactor;
    const newHeight = el.offsetHeight / scaleFactor;
    const currentNode = mindmap.nodes[index];
    if (currentNode.width !== newWidth || currentNode.height !== newHeight) {
      const newNodes = [...mindmap.nodes];
      newNodes[index] = { ...newNodes[index], width: newWidth, height: newHeight };
      setMindmap({ ...mindmap, nodes: newNodes });
    }
  };

  // ドラッグ開始（エッジ追加モードまたは編集中は無効）
  const handleDragStart = (e, index) => {
    if (isEdgeMode) return;
    if (editingNodeId) return;
    const rect = e.target.getBoundingClientRect();
    const offsetX = e.clientX - rect.left;
    const offsetY = e.clientY - rect.top;
    setDragging({ isDragging: true, nodeIndex: index, offsetX, offsetY });
  };

  const handleMouseMove = (e) => {
    if (!dragging.isDragging) return;
    const containerRect = e.currentTarget.getBoundingClientRect();
    const newX = e.clientX - containerRect.left - dragging.offsetX;
    const newY = e.clientY - containerRect.top - dragging.offsetY;
    const newNodes = [...mindmap.nodes];
    newNodes[dragging.nodeIndex] = { ...newNodes[dragging.nodeIndex], x: newX, y: newY };
    setMindmap({ ...mindmap, nodes: newNodes });

    // ゴミ箱との重なり判定
    if (trashRef.current) {
      const trashRect = trashRef.current.getBoundingClientRect();
      const nodeCenterX = newX + (newNodes[dragging.nodeIndex].width || DEFAULT_NODE_WIDTH) / 2;
      const nodeCenterY = newY + (newNodes[dragging.nodeIndex].height || DEFAULT_NODE_HEIGHT) / 2;
      if (
        nodeCenterX >= trashRect.left &&
        nodeCenterX <= trashRect.right &&
        nodeCenterY >= trashRect.top &&
        nodeCenterY <= trashRect.bottom
      ) {
        setTrashHovered(true);
      } else {
        setTrashHovered(false);
      }
    }
  };

  const handleMouseUp = () => {
    if (dragging.isDragging) {
      if (trashHovered) {
        const newNodes = mindmap.nodes.filter((_, idx) => idx !== dragging.nodeIndex);
        setMindmap({ ...mindmap, nodes: newNodes });
      } else {
        // ドラッグ終了時、現在の scale (1.1ならその分打ち消す) を利用してサイズ更新
        const effectiveScale = trashHovered ? 1.0 : 1.1;
        // ※ effectiveScale はドラッグ中のノードに適用される scale です。
        // ここではドラッグ終了後、必ず scale を 1.0 に戻すため、onMouseUp時に handleNodeResize を scale を渡して呼び出します。
        // ※ 各ノードの onMouseUp で呼び出しているため、この部分はここでは不要
      }
      setDragging({ isDragging: false, nodeIndex: null, offsetX: 0, offsetY: 0 });
      setTrashHovered(false);
    }
  };

  const addNode = () => {
    const newNodeId = `node_${Date.now()}`;
    const newNode = {
      nodeId: newNodeId,
      text: '新しいノード',
      x: 100 + Math.random() * 200,
      y: 100 + Math.random() * 200,
      width: DEFAULT_NODE_WIDTH,
      height: DEFAULT_NODE_HEIGHT
    };
    setMindmap({ ...mindmap, nodes: [...mindmap.nodes, newNode] });
  };

  const handleNodeClick = (node, index) => {
    if (isEdgeMode) {
      if (!edgeStart) {
        setEdgeStart(node.nodeId);
      } else {
        if (edgeStart === node.nodeId) {
          setEdgeStart(null);
        } else {
          const newEdge = {
            id: `edge_${Date.now()}`,
            from: edgeStart,
            to: node.nodeId
          };
          const newEdges = mindmap.edges ? [...mindmap.edges, newEdge] : [newEdge];
          setMindmap({ ...mindmap, edges: newEdges });
          setEdgeStart(null);
        }
      }
    }
  };

  const handleEdgeClick = (edgeId, e) => {
    e.stopPropagation();
    if (window.confirm('この線を削除しますか？')) {
      const newEdges = (mindmap.edges || []).filter(edge => edge.id !== edgeId);
      setMindmap({ ...mindmap, edges: newEdges });
    }
  };

  const getNodeById = (nodeId) => {
    return mindmap.nodes.find(n => n.nodeId === nodeId);
  };

  // ダブルクリックで Markdown 編集モードに切替
  const handleNodeDoubleClick = (node) => {
    if (isEdgeMode) return;
    setEditingNodeId(node.nodeId);
    setEditingText(node.text);
  };

  // 編集完了：Enter キー（Shift+Enter で改行可能）で編集完了し、Markdown を保存
  const handleEditFinish = (nodeId) => {
    const newNodes = mindmap.nodes.map(node => {
      if (node.nodeId === nodeId) {
        return { ...node, text: editingText };
      }
      return node;
    });
    setMindmap({ ...mindmap, nodes: newNodes });
    setEditingNodeId(null);
    setEditingText('');
  };

  return (
    <div>
      {/* 操作用ボタン群 */}
      <div style={{ marginBottom: 10 }}>
        <button onClick={addNode}>ノード追加</button>
        <button 
          onClick={() => {
            setIsEdgeMode(!isEdgeMode);
            setEdgeStart(null);
          }}
          style={{ marginLeft: 10 }}
        >
          {isEdgeMode ? 'エッジ追加モード解除' : 'エッジ追加モード'}
        </button>
        {isEdgeMode && (
          <span style={{ marginLeft: 10, color: 'blue' }}>
            {edgeStart ? `開始ノード: ${edgeStart} - 終了ノードを選択` : 'ノードを選択してエッジ開始'}
          </span>
        )}
      </div>

      {/* キャンバス領域 */}
      <div 
        style={{ 
          position: 'relative', 
          width: '100%', 
          height: '500px', 
          border: '1px solid #777',
          overflow: 'hidden'
        }}
        onMouseMove={handleMouseMove}
        onMouseUp={handleMouseUp}
      >
        {/* SVG レイヤー：エッジ描画 */}
        <svg 
          style={{
            position: 'absolute',
            top: 0,
            left: 0,
            width: '100%',
            height: '100%',
            pointerEvents: 'none'
          }}
        >
          {(mindmap.edges || []).map((edge) => {
            const fromNode = getNodeById(edge.from);
            const toNode = getNodeById(edge.to);
            if (!fromNode || !toNode) return null;
            return (
              <line
                key={edge.id}
                x1={fromNode.x + (fromNode.width || DEFAULT_NODE_WIDTH) / 2}
                y1={fromNode.y + (fromNode.height || DEFAULT_NODE_HEIGHT) / 2}
                x2={toNode.x + (toNode.width || DEFAULT_NODE_WIDTH) / 2}
                y2={toNode.y + (toNode.height || DEFAULT_NODE_HEIGHT) / 2}
                stroke="black"
                strokeWidth="2"
                pointerEvents="visibleStroke"
                onClick={(e) => handleEdgeClick(edge.id, e)}
              />
            );
          })}
        </svg>
        {mindmap.nodes.map((node, idx) => {
          // 編集中のノードは scale 常に 1.0
          // ドラッグ中のノードは scale 1.1（trashHovered 時は1.0）
          const isDraggingNode = dragging.isDragging && dragging.nodeIndex === idx;
          const scale =
            editingNodeId === node.nodeId
              ? 1.0
              : isDraggingNode
              ? trashHovered
                ? 1.0
                : 1.1
              : 1.0;
          return (
            <div
              key={node.nodeId}
              style={{
                position: 'absolute',
                left: node.x,
                top: node.y,
                width: node.width || DEFAULT_NODE_WIDTH,
                height: node.height || DEFAULT_NODE_HEIGHT,
                padding: '8px 12px',
                background: idx === 0 ? '#ffe4b2' : '#d1f0d1',
                border: '1px solid #333',
                borderRadius: '4px',
                cursor: isEdgeMode ? 'pointer' : 'move',
                userSelect: 'none',
                transform: `scale(${scale})`,
                transformOrigin: 'center',
                resize: 'both',
                overflow: 'hidden',
                whiteSpace: 'pre-wrap'
              }}
              onMouseDown={(e) => handleDragStart(e, idx)}
              onClick={() => handleNodeClick(node, idx)}
              onDoubleClick={() => handleNodeDoubleClick(node)}
              // onMouseUp 時、現在の scale で割った実サイズを更新
              onMouseUp={(e) => handleNodeResize(idx, e.currentTarget, scale)}
            >
              {editingNodeId === node.nodeId ? (
                <textarea
                  value={editingText}
                  onChange={(e) => setEditingText(e.target.value)}
                  onKeyDown={(e) => {
                    // Enter 単体で編集完了（Shift+Enter で改行）
                    if (e.key === 'Enter' && !e.shiftKey) {
                      e.preventDefault();
                      handleEditFinish(node.nodeId);
                    }
                  }}
                  autoFocus
                  style={{ width: '100%', height: '100%', resize: 'none' }}
                />
              ) : (
                <div 
                  style={{ width: '100%', height: '100%', overflow: 'hidden' }}
                  dangerouslySetInnerHTML={{ __html: marked.parse(node.text, { breaks: true }) }} 
                />
              )}
            </div>
          );
        })}
        {/* ゴミ箱アイコン */}
        <div 
          ref={trashRef}
          style={{
            position: 'absolute',
            bottom: 20,
            left: '50%',
            transform: 'translateX(-50%)',
            pointerEvents: 'none'
          }}
        >
          <svg 
            width={trashHovered ? 50 : 30}
            height={trashHovered ? 50 : 30}
            viewBox="0 0 24 24"
            fill="red"
          >
            <path d="M3 6h18v2H3zm2 3h14l-1.5 12.5a2 2 0 01-2 1.5H8.5a2 2 0 01-2-1.5L5 9zm5 2v9h2v-9H10zm4 0v9h2v-9h-2zM9 4V2h6v2h5v2H4V4z"/>
          </svg>
        </div>
        {/* ありがとうボタン */}
        <div style={{ position: 'absolute', bottom: 10, left: 10 }}>
          <ThankButton mindmapId={mindmap.id} />
        </div>
      </div>
    </div>
  );
}

export default MindmapCanvas;
