const getDisplayTitle = (title) => {
  if (!title) return '';
  // 全角文字が含まれているかどうかを判定
  const isFullWidth = /[^\x00-\xff]/.test(title);
  
  if (isFullWidth) {
    // 全角の場合：13文字以上なら先頭12文字＋「…」
    if (title.length >= 13) {
      return title.substring(0, 12) + '…';
    }
    return title;
  } else {
    // 半角の場合：26文字以上なら先頭24文字で改行を入れる
    if (title.length >= 26) {
      return title.substring(0, 24) + '\n' + title.substring(24);
    }
    return title;
  }
};
