import React from 'react';
import ThankButton from './ThankButton';

/**
 * 簡易版のマインドマップ表示（ノードのリスト表示）
 */
function MindmapCanvas({ mindmap, setMindmap }) {
  const handleNodeChange = (nodeIndex, newText) => {
    const newNodes = [...mindmap.nodes];
    newNodes[nodeIndex].text = newText;
    setMindmap({ ...mindmap, nodes: newNodes });
  };

  return (
    <div style={{ border: '1px solid #777', padding: 10 }}>
      <h3>{mindmap.title}のノード一覧（簡易版）</h3>
      {mindmap.nodes.map((node, idx) => (
        <div key={node.nodeId} style={{ marginBottom: 8 }}>
          <label>ノード {idx + 1}:</label>
          <input
            style={{ marginLeft: 4 }}
            value={node.text}
            onChange={(e) => handleNodeChange(idx, e.target.value)}
          />
        </div>
      ))}
      {/* ありがとうボタン */}
      <ThankButton mindmapId={mindmap.id} />
    </div>
  );
}

export default MindmapCanvas;
