// client/src/pages/Editor.js
import React, { useEffect, useState, useRef } from 'react';
import axios from 'axios';
import { useParams, useNavigate } from 'react-router-dom';
import MindmapCanvas from '../components/MindmapCanvas';

const SERVER_URL = process.env.REACT_APP_SERVER_URL || 'http://localhost:4000';

function Editor() {
  const { mindmapId } = useParams();
  const navigate = useNavigate();
  
  const [mindmap, setMindmap] = useState(null);
  const [title, setTitle] = useState('');
  const [error, setError] = useState(null);
  const [isEditingTitle, setIsEditingTitle] = useState(false);
  const titleInputRef = useRef(null);

  // ありがとうカウント用の state
  const [thankCount, setThankCount] = useState(0);
  // ありがとう送信ボタンの連打防止用
  const [thankDisabled, setThankDisabled] = useState(false);

  useEffect(() => {
    if (mindmapId) {
      fetchMindmap();
      fetchThankCount();
    }
  }, [mindmapId]);

  const fetchMindmap = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/mindmaps/${mindmapId}`);
      setMindmap(res.data);
      setTitle(res.data.title);
      setError(null);
    } catch (err) {
      console.error(err);
      setError(err.response ? err.response.data.error : err.message);
      setMindmap(null);
    }
  };

  const fetchThankCount = async () => {
    try {
      const res = await axios.get(`${SERVER_URL}/api/thank/${mindmapId}`);
      setThankCount(res.data.count);
    } catch (err) {
      console.error(err);
    }
  };

  const handleSave = async () => {
    if (!mindmap) return;
    try {
      await axios.put(`${SERVER_URL}/api/mindmaps/${mindmap.id}`, {
        title,
        nodes: mindmap.nodes,
        edges: mindmap.edges
      });
      alert('保存しました！');
    } catch (err) {
      console.error(err);
      alert('保存に失敗しました');
    }
  };

  const handleShare = () => {
    const shareUrl = window.location.href;
    navigator.clipboard.writeText(shareUrl).then(() => {
      alert('リンクをコピーしました');
    });
  };

  const handleGoHome = () => {
    navigate('/');
  };

  // ありがとう送信（1分間無効化付き）
  const handleSendThank = async () => {
    if (thankDisabled) return;
    try {
      await axios.post(`${SERVER_URL}/api/thank`, { mindmapId });
      await fetchThankCount();
      alert('ありがとうを送りました！');
      setThankDisabled(true);
      // 1分後に再度有効化
      setTimeout(() => {
        setThankDisabled(false);
      }, 60000);
    } catch (err) {
      console.error(err);
      alert('ありがとう送信に失敗しました');
    }
  };

  // タイトル編集のハンドリング
  const handleTitleDoubleClick = () => {
    setIsEditingTitle(true);
  };

  const handleTitleBlur = () => {
    setIsEditingTitle(false);
  };

  const handleTitleKeyDown = (e) => {
    if (e.key === 'Enter') {
      setIsEditingTitle(false);
    }
  };

  if (error) {
    return (
      <div style={{ padding: 20 }}>
        <h1>エラー</h1>
        <p>{error}</p>
        <button onClick={fetchMindmap}>再取得</button>
      </div>
    );
  }

  if (!mindmap) {
    return <div style={{ padding: 20 }}>読み込み中...</div>;
  }

  return (
    <div style={{ padding: 20 }}>
      {/* ヘッダー：ホームボタンとタイトル（編集可能） */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: 10 }}>
        <button
          onClick={handleGoHome}
          style={{
            width: 40,
            height: 40,
            borderRadius: '50%',
            border: 'none',
            background: '#007BFF',
            color: '#fff',
            fontSize: '20px',
            marginRight: 10,
            cursor: 'pointer'
          }}
        >
          ←
        </button>
        {isEditingTitle ? (
          <input
            ref={titleInputRef}
            type="text"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            onBlur={handleTitleBlur}
            onKeyDown={handleTitleKeyDown}
            style={{ fontSize: '24px', flexGrow: 1 }}
          />
        ) : (
          <h2 onDoubleClick={handleTitleDoubleClick} style={{ flexGrow: 1, margin: 0 }}>
            {title}
          </h2>
        )}
      </div>

      {/* ありがとうエリア：大きめの数字と上向き矢印ボタン（ありがとう送信） */}
      <div style={{ display: 'flex', alignItems: 'center', marginBottom: 10 }}>
        <div style={{ fontSize: '32px', fontWeight: 'bold', marginRight: 10 }}>
          {thankCount}
        </div>
        <button
          onClick={handleSendThank}
          disabled={thankDisabled}
          style={{
            border: '1px solid #ccc',
            background: thankDisabled ? '#e0e0e0' : '#f0f0f0',
            padding: '8px 12px',
            cursor: thankDisabled ? 'not-allowed' : 'pointer'
          }}
        >
          ↑
        </button>
        <button onClick={handleShare} style={{ marginLeft: 600 }}>
          SNSで共有
        </button>
      </div>

      {/* キャンバス：スクロール可能なコンテナ */}
      <div
        style={{
          position: 'relative',
          border: '1px solid #ccc',
          width: 800,
          height: 500,
          overflow: 'auto'
        }}
      >
        <MindmapCanvas mindmap={mindmap} setMindmap={setMindmap} />
      </div>

      {/* 下部ボタン：保存＆Export Markdown */}
      <div style={{ marginTop: 20 }}>
        <button onClick={handleSave}>保存</button>
        <button onClick={() => navigate(`/export/${mindmap.id}`)} style={{ marginLeft: 10 }}>
          Export Markdown
        </button>
      </div>
    </div>
  );
}

export default Editor;
